#!/usr/bin/env bash

# Installs relevant known hosts for composer installation.
# To be ran within a build server, not in production.

mkdir -p ~/.ssh
touch ~/.ssh/known_hosts
cat >> ~/.ssh/known_hosts <<- EOM
github.com,192.30.253.113 ssh-rsa 
192.30.253.112 ssh-rsa 
bitbucket.org,104.192.143.2 ssh-rsa 
104.192.143.1 ssh-rsa 
104.192.143.3 ssh-rsa 
bitbucket.com,104.192.143.8 ssh-rsa 
104.192.143.7 ssh-rsa 
EOM
